mapper.update:
UPDATE CART
	SET PRODUCT_AMOUNT = #{product_amount}
		WHERE USER_ID= #{user_id}
		AND PRODUCT_NO = #{product_no}

mapper.select:
SELECT COUNT(*)
	FROM CART
		WHERE USER_ID= #{user_id}
		AND PRODUCT_NO = #{product_no}

mapper.update:
UPDATE cart
	SET PRODUCT_AMOUNT = PRODUCT_AMOUNT + #{product_amount}
		WHERE USER_ID= #{user_id}
		AND PRODUCT_NO = #{product_no}
