mapper.update:
UPDATE CART
	SET PRODUCT_AMOUNT = #{productAmount}
		WHERE USER_ID= #{userId}
		AND PRODUCT_NO = #{productNo}

mapper.select:
SELECT COUNT(*)
	FROM CART
		WHERE USER_ID= #{userId}
		AND PRODUCT_NO = #{productNo}

mapper.update:
UPDATE CART
	SET PRODUCT_AMOUNT = PRODUCT_AMOUNT + #{productAmount}
		WHERE USER_ID= #{userId}
		AND PRODUCT_NO = #{productNo}
